message("===========")
message("stateBrewer")

### Test first case without replicates ###
message("First test case")
strains <- c('BN','SHR','BN','SHR')
marks <- c('H3K4me3','H3K4me3','H3K27me3','H3K27me3')
replicates <- paste0(marks,':',strains)

states2use <- stateBrewer(replicates=replicates)
expect_that(states2use, is_a('numeric'))
expect_that(sort(states2use), is_equivalent_to(0:15))
expect_that(names(sort(states2use)),equals(c("", "H3K27me3:SHR", "H3K27me3:BN", "H3K27me3:BN-H3K27me3:SHR", "H3K4me3:SHR", "H3K4me3:SHR-H3K27me3:SHR", "H3K4me3:SHR-H3K27me3:BN", "H3K4me3:SHR-H3K27me3:BN-H3K27me3:SHR", "H3K4me3:BN", "H3K4me3:BN-H3K27me3:SHR", "H3K4me3:BN-H3K27me3:BN", "H3K4me3:BN-H3K27me3:BN-H3K27me3:SHR", "H3K4me3:BN-H3K4me3:SHR", "H3K4me3:BN-H3K4me3:SHR-H3K27me3:SHR", "H3K4me3:BN-H3K4me3:SHR-H3K27me3:BN", "H3K4me3:BN-H3K4me3:SHR-H3K27me3:BN-H3K27me3:SHR")))

diff.states <- stateBrewer(replicates=replicates, differential.states=TRUE, conditions=strains, tracks2compare=marks)
expect_that(length(diff.states), equals(12))
expect_that(names(sort(diff.states)),equals(c("H3K27me3:SHR", "H3K27me3:BN", "H3K4me3:SHR", "H3K4me3:SHR-H3K27me3:SHR", "H3K4me3:SHR-H3K27me3:BN", "H3K4me3:SHR-H3K27me3:BN-H3K27me3:SHR", "H3K4me3:BN", "H3K4me3:BN-H3K27me3:SHR", "H3K4me3:BN-H3K27me3:BN", "H3K4me3:BN-H3K27me3:BN-H3K27me3:SHR", "H3K4me3:BN-H3K4me3:SHR-H3K27me3:SHR", "H3K4me3:BN-H3K4me3:SHR-H3K27me3:BN")))
expect_that(sort(diff.states), is_equivalent_to(c(1:2, 4:11, 13:14)))

diff.states <- stateBrewer(replicates=replicates, differential.states=TRUE, min.diff=2, conditions=strains, tracks2compare=marks)
expect_that(length(diff.states), equals(4))
expect_that(names(sort(diff.states)),equals(c("H3K4me3:SHR-H3K27me3:SHR", "H3K4me3:SHR-H3K27me3:BN", "H3K4me3:BN-H3K27me3:SHR", "H3K4me3:BN-H3K27me3:BN")))
expect_that(sort(diff.states), is_equivalent_to(c(5:6, 9:10)))

common.states <- stateBrewer(replicates=replicates, common.states=TRUE, conditions=strains, tracks2compare=marks)
expect_that(length(common.states), equals(4))
expect_that(names(sort(common.states)),equals(c("", "H3K27me3:BN-H3K27me3:SHR", "H3K4me3:BN-H3K4me3:SHR", "H3K4me3:BN-H3K4me3:SHR-H3K27me3:BN-H3K27me3:SHR")))
expect_that(sort(common.states), is_equivalent_to(c(0,3,12,15)))


